package org.yawlfoundation.yawl.engine;/** * @author Mike Fowler *         Date: 16-Oct-2006 */public enum YWorkItemStatus {    statusEnabled("Enabled"),    statusFired("Fired"),    statusExecuting("Executing"),    statusComplete("Complete"),    statusIsParent("Is parent"),    statusDeadlocked("Deadlocked"),    statusDeleted("Cancelled"),	  statusForcedComplete("ForcedComplete"),	  statusFailed("Failed"),	  statusSuspended("Suspended"),    statusCancelledByCase("CancelledByCase");    private String status;    YWorkItemStatus(String status)    {        this.status = status;    }    @Override public String toString()    {        return status;    }    public static YWorkItemStatus fromString(String status)    {        if(status.equals(statusComplete.toString())) return statusComplete;        else if(status.equals(statusDeadlocked.toString())) return statusDeadlocked;        else if(status.equals(statusDeleted.toString())) return statusDeleted;        else if(status.equals(statusEnabled.toString())) return statusEnabled;        else if(status.equals(statusExecuting.toString())) return statusExecuting;        else if(status.equals(statusFired.toString())) return statusFired;        else if(status.equals(statusIsParent.toString())) return statusIsParent;        else if(status.equals(statusForcedComplete.toString())) return statusForcedComplete;        else if(status.equals(statusFailed.toString())) return statusFailed;        else if(status.equals(statusSuspended.toString())) return statusSuspended;        else if(status.equals(statusCancelledByCase.toString())) return statusCancelledByCase;        else return null;    }}